                                const map = L.map(mapId).setView([33.645, 10.133466666666667], 8);
                                
                                // OpenStreetMap tiles (free, no API key required)
                                L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                                    attribution: '© OpenStreetMap contributors'
                                }).addTo(map);
                                
                                // Add waypoint markers and route
                                const waypoints = [[33.875,10.75],[32.9297,10.4519],[34.1303,9.1985]];
                                const waypointNames = ["Djerba","tataouine","Matmata"];
                                const markers = [];
                                
                                waypoints.forEach((coord, index) => {
                                    const isFirst = index === 0;
                                    const isLast = index === waypoints.length - 1;
                                    let markerColor = '#007bff';
                                    let markerText = '●';
                                    
                                    if (isFirst) {
                                        markerColor = '#28a745';
                                        markerText = 'START';
                                    } else if (isLast && waypoints.length > 1) {
                                        markerColor = '#dc3545';
                                        markerText = 'END';
                                    }
                                    
                                    const marker = L.circleMarker(coord, {
                                        radius: isFirst || isLast ? 8 : 6,
                                        fillColor: markerColor,
                                        color: 'white',
                                        weight: 2,
                                        opacity: 1,
                                        fillOpacity: 0.9
                                    }).addTo(map);
                                    
                                    marker.bindPopup(`<strong>${waypointNames[index] || 'Stop ' + (index + 1)}</strong><br><small>Click map to view in Google Maps</small>`);
