                                    const polyline = L.polyline(waypoints, {
                                        color: '#007bff',
                                        weight: 4,
                                        opacity: 0.8,
                                        dashArray: '10, 5'
                                    }).addTo(map);
                                    
                                    // Fit map to show all markers
                                    map.fitBounds(polyline.getBounds(), { padding: [20, 20] });
                                }
                                
                                // Click handler for the entire map
                                map.on('click', function() {
                                    window.open('https://www.google.com/maps/dir/Djerba/Tataouine/Matmata/@33.3635178,9.6989879,188500m/data=!3m2!1e3!4b1!4m20!4m19!1m5!1m1!1s0x13aabc09046e9991:0x6830d5301a39ca66!2m2!1d10.8451467!2d33.8075978!1m5!1m1!1s0x1254b7596be92873:0xd94cf811d4ad762d!2m2!1d10.4508956!2d32.9210902!1m5!1m1!1s0x1255ad08c20ec3d3:0x7a7ffdb4ed4ab1d9!2m2!1d9.7678687!2d33.4552061!3e0?authuser=0&entry=ttu&g_ep=EgoyMDI1MDYyMy4yIKXMDSoASAFQAw%3D%3D', '_blank');
