    <!-- Custom Scripts -->
    
        <!-- Leaflet JS for interactive maps -->
        <script src="https://unpkg.com/leaflet@1.9.4/dist/leaflet.js" 
                integrity="sha256-20nQCchB9co0qIjJZRGuk2/Z9VM+kNiyxNV1lvTlZBo=" crossorigin=""></script>
        
        <script>
        // Carousel functionality
        let currentSlide = 0;
        const slides = document.querySelectorAll('.carousel-item');
        const indicators = document.querySelectorAll('.carousel-indicator');

        function showSlide(index) {
            slides.forEach(slide => slide.classList.remove('active'));
            indicators.forEach(indicator => indicator.classList.remove('active'));
            
            if (slides[index]) {
                slides[index].classList.add('active');
                indicators[index] && indicators[index].classList.add('active');
                currentSlide = index;
            }
        }

        function nextSlide() {
            const nextIndex = (currentSlide + 1) % slides.length;
            showSlide(nextIndex);
        }

        function previousSlide() {
            const prevIndex = (currentSlide - 1 + slides.length) % slides.length;
            showSlide(prevIndex);
        }

        // Add click events to indicators
        indicators.forEach((indicator, index) => {
            indicator.addEventListener('click', () => showSlide(index));
        });

        // Auto-advance slides every 5 seconds
        if (slides.length > 1) {
            setInterval(nextSlide, 5000);
        }

        // FAQ functionality
        function toggleFAQ(index) {
            const answer = document.getElementById('faq-' + index);
            const toggle = document.querySelectorAll('.faq-toggle')[index];
            
            if (answer.classList.contains('active')) {
                answer.classList.remove('active');
                toggle.textContent = '+';
            } else {
                // Close all other FAQs
                document.querySelectorAll('.faq-answer').forEach(a => a.classList.remove('active'));
                document.querySelectorAll('.faq-toggle').forEach(t => t.textContent = '+');
                
                // Open this FAQ
                answer.classList.add('active');
                toggle.textContent = '−';
            }
        }

        // Copy exact mobile menu implementation from homepage-s-en.php
        document.addEventListener('DOMContentLoaded', function() {
            // Mobile Menu Toggle
            const mobileToggle = document.getElementById('mobile-toggle');
            const navMenu = document.getElementById('nav-menu');

            mobileToggle.addEventListener('click', function() {
                navMenu.classList.toggle('active');

                if (mobileToggle.querySelector('i').classList.contains('fa-bars')) {
                    mobileToggle.querySelector('i').classList.remove('fa-bars');
                    mobileToggle.querySelector('i').classList.add('fa-times');
                } else {
                    mobileToggle.querySelector('i').classList.remove('fa-times');
                    mobileToggle.querySelector('i').classList.add('fa-bars');
                }
            });

            // Language Dropdown
            const langBtn = document.getElementById('lang-btn');
            const langDropdown = document.getElementById('lang-dropdown');

            langBtn.addEventListener('click', function(e) {
                e.stopPropagation();

                // Toggle dropdown visibility
                langDropdown.classList.toggle('show');

                if (langDropdown.classList.contains('show')) {
                    // Position based on screen size
                    if (window.innerWidth <= 768) {
                        // Mobile: position above
                        langDropdown.style.top = 'auto';
                        langDropdown.style.bottom = '100%';
                        langDropdown.style.marginBottom = '10px';
                    } else {
                        // Desktop: position below
                        langDropdown.style.top = '100%';
                        langDropdown.style.bottom = 'auto';
                        langDropdown.style.marginBottom = '0';
                    }
                }
            });

            // Close dropdown when clicking outside
            document.addEventListener('click', function(e) {
                if (!langBtn.contains(e.target) && !langDropdown.contains(e.target)) {
                    langDropdown.classList.remove('show');
                }
            });

            // Search Modal functionality (copied from homepage-s-en.php)
            const searchBtn = document.getElementById('search-btn');
            const searchModal = document.getElementById('search-modal');
            const searchClose = document.getElementById('search-close');

            if (searchBtn && searchModal && searchClose) {
                searchBtn.addEventListener('click', function(e) {
                    e.preventDefault();
                    searchModal.style.display = 'flex';
                });

                searchClose.addEventListener('click', function() {
                    searchModal.style.display = 'none';
                });

                // Close modal when clicking outside
                window.addEventListener('click', function(e) {
                    if (e.target === searchModal) {
                        searchModal.style.display = 'none';
                    }
                });
            }
            
            // Map iframe error handling
            const mapIframes = document.querySelectorAll('.map-container iframe');
            mapIframes.forEach(iframe => {
                iframe.addEventListener('error', function() {
                    const fallback = this.parentElement.querySelector('.map-fallback');
                    if (fallback) {
                        this.style.display = 'none';
                        fallback.style.display = 'flex';
                    }
                });
                
                // Detect if iframe fails to load (connection refused)
                iframe.addEventListener('load', function() {
                    try {
                        if (this.contentDocument === null) {
                            const fallback = this.parentElement.querySelector('.map-fallback');
                            if (fallback) {
                                this.style.display = 'none';
                                fallback.style.display = 'flex';
                            }
                        }
                    } catch (e) {
                        const fallback = this.parentElement.querySelector('.map-fallback');
                        if (fallback) {
                            this.style.display = 'none';
                            fallback.style.display = 'flex';
                        }
                    }
                });

                // Timeout fallback for connection issues
                setTimeout(() => {
                    if (iframe.style.display !== 'none') {
                        try {
                            if (!iframe.contentDocument || iframe.contentDocument.location.href === 'about:blank') {
                                const fallback = iframe.parentElement.querySelector('.map-fallback');
                                if (fallback) {
                                    iframe.style.display = 'none';
                                    fallback.style.display = 'flex';
                                }
                            }
                        } catch (e) {
                            const fallback = iframe.parentElement.querySelector('.map-fallback');
                            if (fallback) {
                                iframe.style.display = 'none';
                                fallback.style.display = 'flex';
                            }
                        }
                    }
                }, 3000);
            });
        });

        // Smooth scrolling for internal links
        document.querySelectorAll('a[href^="#"]').forEach(anchor => {
            anchor.addEventListener('click', function (e) {
                e.preventDefault();
                const target = document.querySelector(this.getAttribute('href'));
                if (target) {
                    target.scrollIntoView({
                        behavior: 'smooth',
                        block: 'start'
                    });
                }
            });
        });
        </script>
